package com.irctc.dto; import java.time.LocalDate; import java.util.List; import java.math.BigDecimal; public class BookingRequestDto { private Long userId; private Long trainId; private Long sourceStationId; private Long destStationId; private LocalDate travelDate; private List<PassengerDto> passengers; private PaymentDetails paymentDetails; private BigDecimal amount; public Long getUserId(){return userId;} public void setUserId(Long userId){this.userId=userId;} public Long getTrainId(){return trainId;} public void setTrainId(Long trainId){this.trainId=trainId;} public Long getSourceStationId(){return sourceStationId;} public void setSourceStationId(Long sourceStationId){this.sourceStationId=sourceStationId;} public Long getDestStationId(){return destStationId;} public void setDestStationId(Long destStationId){this.destStationId=destStationId;} public java.time.LocalDate getTravelDate(){return travelDate;} public void setTravelDate(java.time.LocalDate travelDate){this.travelDate=travelDate;} public java.util.List<PassengerDto> getPassengers(){return passengers;} public void setPassengers(java.util.List<PassengerDto> passengers){this.passengers=passengers;} public PaymentDetails getPaymentDetails(){return paymentDetails;} public void setPaymentDetails(PaymentDetails paymentDetails){this.paymentDetails=paymentDetails;} public java.math.BigDecimal getAmount(){return amount;} public void setAmount(java.math.BigDecimal amount){this.amount=amount;} }
